import Link from "next/link";
import axios from "axios";
import { getAllPostIds } from "../../filesystem";

function Show({ data }) {
  return <p>{data}</p>;
}

Show.getInitialProps = async (context) => {
  const paths = getAllPostIds();
  const { id } = context.query;

  return {
    data: id,
  };
};

export default Show;
